import React, { useState } from "react";
import { useMoralis } from "react-moralis";
import { Card, Image, Tooltip, Modal, Input, Alert, Spin, Button } from "antd";
import { useNFTBalance } from "hooks/useNFTBalance";
import bytes32 from "bytes32";
import { FileSearchOutlined, ShoppingCartOutlined } from "@ant-design/icons";
import { useMoralisDapp } from "providers/MoralisDappProvider/MoralisDappProvider";
import { getExplorer } from "helpers/networks";
import { useWeb3ExecuteFunction } from "react-moralis";
const { Meta } = Card;

const styles = {
  NFTs: {
    display: "flex",
    flexWrap: "wrap",
    WebkitBoxPack: "start",
    justifyContent: "flex-start",
    margin: "0 auto",
    maxWidth: "1000px",
    gap: "10px",
  },
};

function NFTBalance() {
  const { NFTBalance, fetchSuccess } = useNFTBalance();
  const { chainId, marketAddress, contractABI,marketPlaceBoilerAddress,marketPlaceBoilerABI,ERC721ABI,demotokenAddress} = useMoralisDapp();
  const { Moralis } = useMoralis();
  const [visible, setVisibility] = useState(false);
  const [nftToSend, setNftToSend] = useState(null);
  const [price, setPrice] = useState(1);
  const [loading, setLoading] = useState(false);
  const contractProcessor = useWeb3ExecuteFunction();
  const contractABIJson = JSON.parse(marketPlaceBoilerABI);
  const listItemFunction = "createMarketItem";
  const ItemImage = Moralis.Object.extend("ItemImages");

  async function list(nft, listPrice,v,r,s,nounce) {
    
    console.log(nft.token_address);
    console.log(nft.token_id);
    console.log(String(listPrice));
    console.log(nounce," ",v," ",s," ",r," ");
    setLoading(true);
    const ops = {
      contractAddress: marketPlaceBoilerAddress,
      functionName: listItemFunction,
      abi: JSON.parse(marketPlaceBoilerABI),
      params: {
        nftContract: nft.token_address,
        tokenId: nft.token_id,
        price: listPrice,
        NFTtype: String(nft.contract_type),
        nounce:nounce,
        r:v,
        v:String(r),
        s:String(s)
      },
    };
    
    await contractProcessor.fetch({
      params: ops,
      onSuccess: () => {
        console.log("success");
        setLoading(false);
        setVisibility(false);
        addItemImage();
        succList();
      },
      onError: (error) => {
        console.log(error);
        setLoading(false);
        failList();
      },
    });
  }

  async function authenticate(nft, listPrice){
    const usrAddress = Moralis.User.Address;

    let nounce= Math.floor(Math.random() * 1000000000);
    let message="Nounce: "+nounce +" Hello You Are About to sign a transaction ";
    let newMessage="expiry:"+(Math.floor(Date.now() / 1000)+86400*60)+
    "nonce:"+nounce+
    "signer:"+
   
    "wallet:"+usrAddress+
    "token:"+nft.token_address+
    "amount:"+0+
    "id:"+nft.id+
    "sender:"+
    "kind:"+
    0x36372b07+
    "wallet:"+
    0x0000000000000000000000000000000000000000+
    "token:"+
    demotokenAddress+
    "amount:"+
    listPrice+
    "id:"
    +0+
    "affiliate:"
    +"kind:"+
    0x36372b07+
    "wallet:"+
    0xff98f0052bda391f8fad266685609ffb192bef25+
    "token:"+
    0x0000000000000000000000000000000000000000+
    "amount:"
    +0+
    "id:"+
    "0";
    var user = await Moralis.Web3.authenticate({ signingMessage: newMessage });
    let signature= user.get("authData");
    let sig=signature.moralisEth.signature
    console.log(sig);
    let signaturenew = sig.slice(2, 132)
    var r = `${sig.slice(0, 64)}`
    var s = `0x${sig.slice(64, 128)}`
    var v =  parseInt(sig.slice(128, 130), 16) %2 + 27;
    console.log(r,"r");
    console.log(s,"s");
    console.log(v,"v");
    list(nft,listPrice,v,r,s,nounce);
  }
  async function approveAll(nft) {
    setLoading(true);
    let ops='';
    console.log(nft.contract_type);
    if(nft.contract_type=="ERC1155"){
      console.log("ERC1155 For approval");
      ops = {
        contractAddress: nft.token_address,
        functionName: "setApprovalForAll",
        abi: [{"inputs":[{"internalType":"address","name":"operator","type":"address"},{"internalType":"bool","name":"approved","type":"bool"}],"name":"setApprovalForAll","outputs":[],"stateMutability":"nonpayable","type":"function"}],
        params: {
          operator: marketPlaceBoilerAddress,
          approved: true
        },
      };
    }else{ 
      console.log("ERC721 For approval");
     ops = {
      contractAddress: nft.token_address,
      functionName: "approve",
      abi: JSON.parse(ERC721ABI),
      params: {
        to: marketAddress,
        tokenId: nft.token_id
      },
    };
  }
    await contractProcessor.fetch({
      params: ops,
      onSuccess: () => {
        console.log("Approval Received");
        setLoading(false);
        setVisibility(false);
        succApprove();
      },
      onError: (error) => {
        console.log(error);
        setLoading(false);
        failApprove();
      },
    });
  }

  const handleSellClick = (nft) => {
    setNftToSend(nft);
    setVisibility(true);
  };

  function succList() {
    let secondsToGo = 5;
    const modal = Modal.success({
      title: "Success!",
      content: `Your NFT was listed on the marketplace`,
    });
    setTimeout(() => {
      modal.destroy();
    }, secondsToGo * 1000);
  }

  function succApprove() {
    let secondsToGo = 5;
    const modal = Modal.success({
      title: "Success!",
      content: `Approval is now set, you may list your NFT`,
    });
    setTimeout(() => {
      modal.destroy();
    }, secondsToGo * 1000);
  }

  function failList() {
    let secondsToGo = 5;
    const modal = Modal.error({
      title: "Error!",
      content: `There was a problem listing your NFT`,
    });
    setTimeout(() => {
      modal.destroy();
    }, secondsToGo * 1000);
  }

  function failApprove() {
    let secondsToGo = 5;
    const modal = Modal.error({
      title: "Error!",
      content: `There was a problem with setting approval`,
    });
    setTimeout(() => {
      modal.destroy();
    }, secondsToGo * 1000);
  }

  function addItemImage() {
    const itemImage = new ItemImage();

    itemImage.set("image", nftToSend.image);
    itemImage.set("nftContract", nftToSend.token_address);
    itemImage.set("tokenId", nftToSend.token_id);
    itemImage.set("name", nftToSend.name);

    itemImage.save();
  }

  return (
    <>
      <div style={styles.NFTs}>
        {contractABIJson.noContractDeployed && (
          <>
            <Alert
              message="No Smart Contract Details Provided. Please deploy smart contract and provide address + ABI in the MoralisDappProvider.js file"
              type="error"
            />
            <div style={{ marginBottom: "10px" }}></div>
          </>
        )}
        {!fetchSuccess && (
          <>
            <Alert
              message="Unable to fetch all NFT metadata... We are searching for a solution, please try again later!"
              type="warning"
            />
            <div style={{ marginBottom: "10px" }}></div>
          </>
        )}
        {NFTBalance &&
          NFTBalance.map((nft, index) => (
            <Card
              hoverable
              actions={[
                <Tooltip title="View On Blockexplorer">
                  <FileSearchOutlined
                    onClick={() =>
                      window.open(
                        `${getExplorer(chainId)}address/${nft.token_address}`,
                        "_blank"
                      )
                    }
                  />
                </Tooltip>,
                <Tooltip title="List NFT for sale">
                  <ShoppingCartOutlined onClick={() => handleSellClick(nft)} />
                </Tooltip>,
              ]}
              style={{ width: 240, border: "2px solid #e7eaf3" }}
              cover={
                <Image
                  preview={false}
                  src={nft?.image || "error"}
                  fallback="data:image/png;base64,"
                  alt=""
                  style={{ height: "240px" }}
                />
              }
              key={index}
            >
              <Meta title={nft.name} description={nft.contract_type} />
            </Card>
          ))}
      </div>

      <Modal
        title={`List ${nftToSend?.name} #${nftToSend?.token_id} For Sale`}
        visible={visible}
        onCancel={() => setVisibility(false)}
        onOk={() => authenticate(nftToSend, price)}
        okText="List"
        footer={[
          <Button onClick={() => setVisibility(false)}>
            Cancel
          </Button>,
          <Button onClick={() => approveAll(nftToSend)} type="primary">
            Approve
          </Button>,
          <Button onClick={() => authenticate(nftToSend, price)} type="primary">
            List
          </Button>
        ]}
      > 
        <Spin spinning={loading}>
          <img
            src={`${nftToSend?.image}`}
            style={{
              width: "250px",
              margin: "auto",
              borderRadius: "10px",
              marginBottom: "15px",
            }}
          />
          <Input
            autoFocus
            placeholder="Listing Price in DEMO"
            onChange={(e) => setPrice(e.target.value)}
          />
          
        </Spin>
      </Modal>
    </>
  );
}

export default NFTBalance;
